function [Xfiltered, Yfiltered, channels] = getBackusDataset(subject_nr) 
    data_path = getenv('data_path');
    input=load([data_path, 'ltmcla_S0', num2str(subject_nr), '.mat']);
    if (subject_nr == 8)
        input=input.ltmcla_S08;
    elseif (subject_nr == 9)
        input=input.ltmcla_S09;
    else
        error('wrong subject number\n');
    end
    X = zeros(size(input.data,2), size(input.data{1},1), size(input.data{1},2));    
    Y = zeros(size(input.data,2), 1); 
    for i = 1:size(input.data,2)
        X(i, :, :) = input.data{i}(:, :);    
        Y(i) = input.design(i);
    end
    Xfiltered = X(Y ~= 0, :, :); 
    Yfiltered = Y(Y ~= 0); 
    Yfiltered(Yfiltered == 201) = 1;
    Yfiltered(Yfiltered == 202) = -1; 
    Yfiltered(Yfiltered == 203) = 1;
    Yfiltered(Yfiltered == 204) = -1;        
    writeLog(0, 'Raw data loaded\n');
